sys_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
   var action = g_form.getActionName();
   //If the 'close_incident' button was clicked and 'close_notes' field is empty
   if(action == 'send_bill_to_customer' && g_form.getValue('u_service_amount') == 0){
      //Make 'close_notes' mandatory and abort the submission
      g_form.setMandatory('close_notes',true);
	   g_form.addInfoMessage('Please enter the Service Amount befor submit!');
      return false;
   }
}
