sys_script.script:
(function executeRule(current, previous /*null when async*/) {
var ouList =[];
var otb = new GlideRecord('x_nios2_onetimebil_otb_ao');

otb.query();
	while(otb.next()) {
		//gs.info(otb.u_organization_code);
		ouList.push(otb.getDisplayValue('u_organization_code'));

	}
//if(current.u_administrative_officer == false && (current.u_organizational_unit.u_id ==0 || current.u_organizational_unit.u_id == 13 || current.u_organizational_unit.u_id == 18 || current.u_organizational_unit.u_id == 19)) {
	if(current.u_administrative_officer == false && ouList.indexOf(current.u_organizational_unit)>=0) {
	//If no longer an AO, remove from one time bill AO group

	var gr = new GlideRecord("sys_user_grmember");
	gr.addQuery('group','efcd52fcdbbdaf40881ef30e0f961936');
	gr.addQuery('user', current.sys_id);
	gr.query();
	gr.deleteMultiple();	
}
	else if (current.u_administrative_officer == true && (current.u_organizational_unit.u_id ==0 || current.u_organizational_unit.u_id == 13 || current.u_organizational_unit.u_id == 18 || current.u_organizational_unit.u_id == 19)) {
		//check if user exist
		var su = new GlideRecord("sys_user_grmember");
	su.addQuery('group','efcd52fcdbbdaf40881ef30e0f961936');
	su.addQuery('user', current.sys_id);
	su.query();
		if (su.next()){
			//don nothing
		}
		else{
			var grmember = new GlideRecord('sys_user_grmember');
		    grmember.initialize();
		    grmember.user = current.sys_id;
		    grmember.group = 'efcd52fcdbbdaf40881ef30e0f961936';
		    grmember.insert();

		}
	}

})(current, previous);
