sys_ui_action.script:
function checkAmount(){

	gsftSubmit(null, g_form.getFormElement(), 'approve_bill_by_customer');
}

//Code that runs without 'onclick'
//Ensure call to server-side function with no browser errors
if(typeof window == 'undefined')
	setStaus();


function setStaus(){
	//check customer ptc
	var billAmount =0;

	billAmount = current.u_service_amount;

	var totalAmount =0 ;
	var flag = 0;
	var otb = new GlideRecord('x_nios2_onetimebil_one_time_line_items');
	otb.addQuery('u_bill_number.u_bill_number', current.u_bill_number);
	otb.query();
	while(otb.next()) {
		//check empty amount if ptc exist
		if (otb.u_amount !=''){
		    totalAmount = totalAmount + parseFloat(otb.u_amount);
		}
		if (otb.u_customer_ptc =='' || otb.u_amount =='' ){
			flag =1;
			gs.addInfoMessage('Customer PTC and Amount cannot be empty.');
		   current.setAbortAction(true);


		}
	}

	if (flag == 0){
	totalAmount = totalAmount.toFixed(2);
	billAmount = parseFloat(billAmount).toFixed(2);
	if (totalAmount != billAmount){
			gs.addInfoMessage('Service amount must be equal to the total of Customer amount.');
		current.setAbortAction(true);
	}
	else{
	//after validate set status to Approved
	current.u_status = 'Approved';	
	current.update();
	gs.addInfoMessage('Bill has been approved.');

	action.setRedirectURL(current);	
}}
}
