sys_ui_action.script:
function checkAmount(){

	gsftSubmit(null, g_form.getFormElement(), 'send_bill_to_customer');
}

//Code that runs without 'onclick'
//Ensure call to server-side function with no browser errors
if(typeof window == 'undefined')
	setStaus();


function setStaus(){

	//check provider ptc
	var billAmount =0;

	billAmount = current.u_service_amount;

	var totalAmount =0 ;

	var otb = new GlideRecord('x_nios2_onetimebil_provider_ptc');
	otb.addQuery('u_bill.u_bill_number', current.u_bill_number);
	otb.query();
	while(otb.next()) {
		if (otb.u_amount!=''){
			totalAmount = totalAmount + parseFloat(otb.u_amount);
		}
		if (otb.u_provider_ptc =='' || otb.u_amount ==''){
			gs.addInfoMessage('Service PTC and Amount can not be empty.');

			current.setAbortAction(true);
		}
	}
        totalAmount = totalAmount.toFixed(2);
	billAmount = parseFloat(billAmount).toFixed(2);
	//gs.info( 'total mamount: ' + totalAmount + ' billamount: ' +billAmount );
	if (totalAmount != billAmount){
		gs.addInfoMessage('Service amount must be equal to the total of provider amount.');
		current.setAbortAction(true);
	}
	else{
		var currentdate = new GlideDate();
		currentdate.getByFormat("MM-dd-yyyy");
		//after validate set status to pending
		current.u_status = 'Pending';
		current.u_date_sent = currentdate;
		current.update();
		gs.addInfoMessage('Bill has been sent.');
		action.setRedirectURL(current);
	}

}
